mapper.select:
SELECT COUNT(*)
FROM charging_order
WHERE deleted = 0
AND tenant_id = #{tenantId}
AND create_time BETWEEN #{startTime} AND #{endTime}

SELECT COALESCE(SUM(charging_amount), 0)
FROM charging_order
WHERE deleted = 0
AND tenant_id = #{tenantId}
AND create_time BETWEEN #{startTime} AND #{endTime}
AND status IN ('COMPLETED', 'PAID')

SELECT COALESCE(SUM(total_amount), 0)
FROM charging_order
WHERE deleted = 0
AND tenant_id = #{tenantId}
AND create_time BETWEEN #{startTime} AND #{endTime}
AND status IN ('COMPLETED', 'PAID')
